"use strict";

import { expect } from "chai";
import dedup from "../index.mjs";
describe("test dedup", function () {
  it("should dedup with a,b,a", function () {
    var a = ["a", "b", "a"];
    var acture = dedup(a);
    expect(acture).to.deep.equal(["a", "b"]);
  });
  it("should dedup with a,a,a", function () {
    var a = ["a", "a", "a"];
    var acture = dedup(a);
    expect(acture).to.deep.equal(["a"]);
  });
  it("should dedup with a,a,b", function () {
    var a = ["a", "a", "b"];
    var acture = dedup(a);
    expect(acture).to.deep.equal(["a", "b"]);
  });
  it("should dedup with b,a,a", function () {
    var a = ["b", "a", "a"];
    var acture = dedup(a);
    expect(acture).to.deep.equal(["b", "a"]);
  });
  describe("test dedup reverse", function () {
    it("should dedup with a,b,a", function () {
      var a = ["a", "b", "a"];
      var acture = dedup(a, true);
      expect(acture).to.deep.equal(["b", "a"]);
    });
  });
});