import { expect } from "chai";
import { defaultQuery as query } from "../queryFrom.mjs";
describe("Test query all", function () {
  after(function () {
    return document.body.innerHTML = "";
  });
  it("test super queryAll", function () {
    document.body.innerHTML = "\n<main id=\"root\"><div></div><span></span></main>\n";
    var acture = query.all(["main", "div", "span"]);
    expect(acture.length).to.be.equal(3);
  });
  it("test query nothing", function () {
    var acture1 = query.all(null);
    var acture2 = query.all(undefined);
    var acture3 = query.all();
    expect([acture1, acture2, acture3]).to.deep.equal([null, undefined, undefined]);
  });
});