import { expect } from "chai";
import queryFrom from "../queryFrom.mjs";
describe("Test", function () {
  after(function () {
    return document.body.innerHTML = "";
  });
  it("sample test", function () {
    var query = queryFrom(function () {
      return document;
    });
    var name = query.one("body").nodeName;
    expect(name).to.be.equal("BODY");
  });
  it("test base is not func", function () {
    var query = queryFrom(document);
    var name = query.one("body").nodeName;
    expect(name).to.be.equal("BODY");
  });
  it("test base is undefined", function () {
    var query = queryFrom(undefined);
    expect(query).to.be.false;
  });
});