import { expect } from "chai";
import get from "../get.mjs";
describe("Test Get Object Value", function () {
  it("test get", function () {
    var a = {
      foo: {
        bar: {
          key: "v"
        }
      }
    };
    var acture = get(a, ["foo", "bar", "key"]);
    expect(acture).to.equal("v");
  });
  it("test with null", function () {
    var a = {
      foo: {
        bar: {
          key: null
        }
      }
    };
    var acture = get(a, ["foo", "bar", "key"]);
    expect(acture).to.equal(null);
  });
  it("test with number", function () {
    var a = {
      foo: 111
    };
    var acture = get(a, ["foo", "bar"]);
    expect(acture).to.equal(undefined);
  });
  it("test get map", function () {
    var a = {
      get: function get() {
        return {
          b: {
            c: {
              get: function get() {
                return "d";
              },
              size: 1
            }
          }
        };
      },
      size: 1
    };
    var acture = get(a, ["foo-any", "b", "c", "foo-any"]);
    expect(acture).to.equal("d");
  });
});