import { expect } from "chai";
import toJS from "../toJS.mjs";
describe("Test toJS", function () {
  it("test without toJS", function () {
    var o = {
      foo: "bar"
    };
    expect(toJS(o)).to.deep.equal({
      foo: "bar"
    });
  });
  it("test has toJS", function () {
    var o = {
      foo1: "bar1",
      foo2: "bar2",
      toJS: function toJS() {
        return Object.keys(o).filter(function (v) {
          return v !== "toJS";
        });
      }
    };
    expect(toJS(o)).to.deep.equal(["foo1", "foo2"]);
  });
});