import { expect } from "chai";
import { safeMatch } from "../index.mjs";
describe("test safe match", function () {
  it("basic test", function () {
    var actual = safeMatch("", /1/);
    expect(actual).to.be["null"];
  });
  it("test null", function () {
    var actual = safeMatch(null, /1/);
    expect(actual).to.be["null"];
  });
  it("test undefined", function () {
    var actual = safeMatch(undefined, /1/);
    expect(actual).to.be["null"];
  });
  it("test object", function () {
    var actual = safeMatch({}, /1/);
    expect(actual).to.be["null"];
  });
  it("test int", function () {
    var actual = safeMatch(1, /1/);
    expect(actual[0]).to.equal("1");
  });
  it("test empty reg", function () {
    var actual = safeMatch(1, undefined);
    expect(actual[0]).to.equal("");
  });
});