import { expect } from "chai";
import { getSinon } from "reshow-unit";
import DLog from "../dlog.mjs";
describe("Dlog Test", function () {
  it("pass empty", function () {
    var log = new DLog();
    log.info();
    [false, null, undefined].forEach(function (item) {
      return log.info(item);
    });
  });
  it("is not data set", function () {
    var sinon = getSinon();
    var consoleTable = sinon.spy(console, "table");
    var consoleInfo = sinon.spy(console, "info");
    var log = new DLog();
    log.info([0, 1, null, undefined, false]);
    expect(consoleTable.called).to.be.false;
    expect(consoleInfo.called).to.be.true;
  });
  it("is not data set - another sample", function () {
    var sinon = getSinon();
    var consoleTable = sinon.spy(console, "table");
    var consoleInfo = sinon.spy(console, "info");
    var log = new DLog();
    log.info({
      0: {
        0: 111,
        1: 222
      },
      1: "333"
    });
    expect(consoleTable.called).to.be.false;
    expect(consoleInfo.called).to.be.true;
  });
  it("is data set", function () {
    var sinon = getSinon();
    var consoleTable = sinon.spy(console, "table");
    var consoleInfo = sinon.spy(console, "info");
    var log = new DLog();
    log.info({
      0: {
        a: 1
      },
      1: {
        a: 2
      }
    });
    expect(consoleTable.called).to.be.true;
    expect(consoleInfo.called).to.be.true;
  });
});