import { expect } from "chai";
import applyStyles from "../applyStyles.mjs";
import StyleObject from "../StyleObject.mjs";
import store from "../store.mjs";
describe("Test applyStyles", function () {
  it("test empty className", function () {
    var oStyle = new StyleObject({}, "", "c1_");
    store.registry.c1_ = true;
    expect(oStyle.isCompiled()).to.be.true;
    var props = applyStyles({}, oStyle, 1);
    expect(props).to.deep.equal([{
      className: "c1_ c1_1"
    }]);
  });
  it("test has className", function () {
    var oStyle = new StyleObject({}, "", "c1_");
    store.registry.c1_ = true;
    expect(oStyle.isCompiled()).to.be.true;
    var props = applyStyles({
      className: "fake"
    }, oStyle, 1);
    expect(props).to.deep.equal([{
      className: "fake c1_ c1_1"
    }]);
  });
});