import { expect } from "chai";
import { URL } from "../urlStore.mjs";
describe("Test url object", function () {
  it("basic test", function () {
    var oUrl = new URL();
    expect(!!oUrl).to.be.true;
  });
  it("test cook loc key", function () {
    var oUrl = new URL();
    var ukey = ":foo";
    var actual = oUrl.getLocKey(ukey);
    expect(actual).to.equal("foo");
  });
  it("test get pathname", function () {
    var oUrl = new URL({
      pathname: "/foo/bar"
    });
    var actual = oUrl.get(":pathname");
    expect(actual).to.deep.equal(["", "foo", "bar"]);
  });
});