"use strict";

import toPercent, { percent, round, toNum, getNum, toInt } from "../index.mjs";
import { expect } from "chai";
describe("Test to percent", function () {
  var n = 1;
  it("To string", function () {
    expect(toPercent(n)).to.equal("100.00%");
  });
});
describe("Test round", function () {
  it("Test float", function () {
    expect(round(2.225)).to.equal("2.23");
  });
  it("Test int", function () {
    expect(round(1)).to.equal("1.00");
  });
  it("Test float string", function () {
    expect(round("2.225")).to.equal("2.23");
  });
  it("Test int string", function () {
    expect(round("1")).to.equal("1.00");
  });
  it("Test 0", function () {
    expect(round(0)).to.equal("0.00");
  });
});
describe("Test percent", function () {
  it("Test 1", function () {
    expect(percent(1)).to.equal("100.00");
  });
  it("Test empty", function () {
    expect(percent(undefined)).to.equal("0.00");
  });
});
describe("to num", function () {
  it("Test bool", function () {
    expect(toNum(true)).to.equal(1);
    expect(toNum(false)).to.equal(0);
  });
  it("Test empty", function () {
    expect(toNum([])).to.equal(0);
    expect(toNum(null)).to.equal(0);
    expect(toNum("")).to.equal(0);
  });
  it("Test int", function () {
    expect(toNum(1)).to.equal(1);
  });
  it("Test float", function () {
    expect(toNum(1.1)).to.equal(1.1);
  });
  it("Test int string", function () {
    expect(toNum("1")).to.equal(1);
  });
  it("Test float string", function () {
    expect(toNum("1.1")).to.equal(1.1);
  });
  it("Test Hexadecimal", function () {
    expect(toNum(0xa)).to.equal(10);
  });
  it("Test 4.89e7", function () {
    expect(toNum(4.89e7)).to.equal(48900000);
    expect(toNum("4.89e7")).to.equal(48900000);
  });
  it("Test 100.01f", function () {
    expect(toNum("100.01f")).to.equal(100.01);
  });
  it("Test over max-int", function () {
    var overInt = "20210106042517623";
    expect(toNum(overInt)).to.equal(overInt);
  });
});
describe("To Int", function () {
  it("Test from string", function () {
    expect(toInt("1")).to.equal(1);
  });
  it("Test from float", function () {
    expect(toInt(1.1)).to.equal(1);
    expect(toInt(1.5)).to.equal(2);
  });
  it("Test from float string", function () {
    expect(toInt("1.1")).to.equal(1);
    expect(toInt("1.5")).to.equal(2);
  });
  it("Test zero", function () {
    expect(toInt("-0")).to.equal(0);
  });
});
describe("Get Num", function () {
  it("Test input number", function () {
    var intV = 1;
    expect(getNum(intV)).to.equal(intV);
    var flotV = 0.1;
    expect(getNum(flotV)).to.equal(flotV);
  });
  it("Test input object", function () {
    var v = {};
    expect(getNum(v)).to.equal(0);
  });
  it("Test get int", function () {
    var s = "cd2";
    expect(getNum(s)).to.equal(2);
  });
  it("Test get multi int", function () {
    var s = "cd22";
    expect(getNum(s)).to.equal(22);
  });
  it("Test get float", function () {
    var s = "cd2.1";
    expect(getNum(s)).to.equal(2.1);
  });
  it("Test get multi float", function () {
    var s = "cd20.13";
    expect(getNum(s)).to.equal(20.13);
  });
  it("Test get multi float and end with zero", function () {
    var s = "cd20.10";
    expect(getNum(s)).to.equal(20.1);
  });
  it("Test negative number", function () {
    var s = "cd-2.1";
    expect(getNum(s)).to.equal(-2.1);
  });
});