import { expect } from "chai";
import toArray from "../index.mjs";
describe("Test any-to-array", function () {
  it("conver array", function () {
    var actual = toArray([0]);
    expect(actual).to.deep.equal([0]);
  });
  it("conver non-array", function () {
    var actual = toArray(0);
    expect(actual).to.deep.equal([0]);
  });
  it("pass empty (undefined)", function () {
    var actual = toArray();
    expect(actual).to.deep.equal([]);
  });
  it("pass null", function () {
    var actual = toArray(null);
    expect(actual).to.deep.equal([null]);
  });
});