import { expect } from "chai";
import { oneItemArrayToString } from "../index.mjs";
describe("Test oneItemArrayToString", function () {
  it("basic test", function () {
    var a = ["foo"];
    expect(oneItemArrayToString(a)).to.equal("foo");
  });
  it("test keep string", function () {
    var a = "bar";
    expect(oneItemArrayToString(a)).to.equal("bar");
  });
});