import { expect } from "chai";
import { testString } from "../index.mjs";
describe("Test testString", function () {
  it("input 0", function () {
    var num = 0;
    expect(testString(num)).to.be["true"];
  });
  it("input 1", function () {
    var num = 1;
    expect(testString(num)).to.be["true"];
  });
  it("input null", function () {
    var val = null;
    expect(testString(val)).to.be["true"];
  });
  it("input undefined", function () {
    var val = undefined;
    expect(testString(val)).to.be["true"];
  });
  it("input ''", function () {
    var val = "";
    expect(testString(val)).to.be["true"];
  });
  it("input {}", function () {
    var val = {};
    expect(testString(val)).to.be["false"];
  });
  it("input []", function () {
    var val = [];
    expect(testString(val)).to.be["false"];
  });
});