var dedup = function dedup(array, reverse) {
  if (!array || !array.filter) {
    return array;
  } // This way could keep array in same position


  if (reverse) {
    return array.filter(function (item, pos, arr) {
      return arr.lastIndexOf(item) === pos;
    });
  } else {
    return array.filter(function (item, pos, arr) {
      return arr.indexOf(item) === pos;
    });
  }
};

export default dedup;