import { expect } from "chai";
import has from "../has.mjs";
describe("Test Has Object Value", function () {
  it("test has object", function () {
    var o = {
      foo: "bar"
    };
    expect(has(o, "foo")).to.be.true;
    expect(has(o, "bar")).to.be.false;
  });
  it("test has empty object", function () {
    var o = undefined;
    expect(has(o, "foo")).to.be.false;
  });
  it("test has map", function () {
    var a = {
      has: function has(k) {
        return k === "foo" ? true : false;
      },
      size: 1
    };
    expect(has(a, "foo")).to.be.true;
    expect(has(a, "bar")).to.be.false;
  });
});