import { expect } from "chai";
import toMap from "../toMap.mjs";
describe("Test toMap", function () {
  it("test toMap", function () {
    var map = {
      a: {
        toJS: function toJS() {
          return "foo";
        }
      },
      b: {
        toJS: function toJS() {
          return "bar";
        }
      }
    };
    expect(toMap(map)).to.deep.equal({
      a: "foo",
      b: "bar"
    });
  });
  it("test with immutable map", function () {
    var map = [{
      toJS: function toJS() {
        return "foo";
      }
    }, {
      toJS: function toJS() {
        return "bar";
      }
    }];
    var actual = toMap(map);
    expect(actual).to.deep.equal({
      0: "foo",
      1: "bar"
    });
  });
});