import { expect } from "chai";
import getSafeReg from "../index.mjs";
describe("test get safe reg", function () {
  var n;
  it("test get safe reg", function () {
    n = getSafeReg("/[|\\{}()[]^$+*?.]/g");
    expect(n).to.equal("/\\[\\|\\\\\\{\\}\\(\\)\\[\\]\\^\\$\\+\\*\\?\\.\\]/g");
  });
  it("test reg is valid", function () {
    var patt = new RegExp(n);
    var res = patt.test("/[|\\{}()[]^$+*?.]/g");
    expect(res).to.be["true"];
  });
  it("test with undefined", function () {
    var acture = getSafeReg();
    expect(acture).to.equal("");
  });
});