import { expect } from "chai";
import mergeDefaultValue from "../mergeDefaultValue.mjs";
describe("Test merge default value", function () {
  it("default value is not exists", function () {
    var o = {};
    var actural = mergeDefaultValue(o, {
      foo: "bar"
    });
    var expected = {
      foo: "bar"
    };
    expect(actural).to.deep.equal(expected);
  });
  it("default value is exists", function () {
    var o = {
      foo: "bar1"
    };
    var actural = mergeDefaultValue(o, {
      foo: "bar"
    });
    var expected = {
      foo: "bar1"
    };
    expect(actural).to.deep.equal(expected);
  });
  it("default value is object", function () {
    var o = {
      foo: {
        bar1: "a"
      }
    };
    var actural = mergeDefaultValue(o, {
      foo: {
        bar2: "b"
      }
    });
    var expected = {
      foo: {
        bar1: "a",
        bar2: "b"
      }
    };
    expect(actural).to.deep.equal(expected);
  });
  it("default value is object and exists", function () {
    var o = {
      foo: {
        bar1: "a"
      }
    };
    var actural = mergeDefaultValue(o, {
      foo: {
        bar1: "b"
      }
    });
    var expected = {
      foo: {
        bar1: "a"
      }
    };
    expect(actural).to.deep.equal(expected);
  });
  it("merge with null", function () {
    var o = null;
    var actural = mergeDefaultValue(o, {
      foo: "bar"
    });
    var expected = {
      foo: "bar"
    };
    expect(actural).to.deep.equal(expected);
  });
});