import { expect } from "chai";
import ucfirst from "../index";
describe("Test ucfirst-js", function () {
  it("basic test", function () {
    var actual = ucfirst();
    expect(actual).to.be["null"];
  });
  it("test null", function () {
    var actual = ucfirst(null);
    expect(actual).to.be["null"];
  });
  it("test undefined", function () {
    var actual = ucfirst(undefined);
    expect(actual).to.be["null"];
  });
  it("test foo", function () {
    var actual = ucfirst("foo");
    expect(actual).to.equal("Foo");
  });
});