import { expect } from "chai";
import { arrayToObject } from "../index.mjs";
describe("Test arrayToObject", function () {
  it("test arrayToObject", function () {
    var a = [{
      foo: 1,
      bar: 2
    }, {
      foo: 3,
      bar: 4
    }];
    var acture = arrayToObject(a, "foo");
    var expected = {
      1: {
        foo: 1,
        bar: 2
      },
      3: {
        foo: 3,
        bar: 4
      }
    };
    expect(acture).to.deep.equal(expected);
  });
});